select distinct wenshu_info.* from wenshu_info 



            AND wenshu_info.penalty_name = #{penalty_definite}


             AND wenshu_info.penalty_name in (select penalty_map.penalty_name_definite from penalty_map where penalty_map.penalty_class = #{penalty_class})




     AND   wenshu_info.id in (select province.id from province where province.province_address = #{province_address})



      AND  wenshu_info.id in (select province.id from province where province.province_residence = #{province_residence})



LIMIT #{page_start},#{page_size}

select IFNULL(SUM(province_penaltyDefinite.number),0)from province_penaltyDefinite 



            AND province_penaltyDefinite.penalty_name = #{penalty_definite}


             AND province_penaltyDefinite.penalty_name in (select penalty_map.penalty_name_definite from penalty_map where penalty_map.penalty_class = #{penalty_class})




        AND   province_penaltyDefinite.province_address = #{province_address}



        AND  province_penaltyDefinite.province_residence = #{province_residence}

select distinct wenshu_info.* from wenshu_info 


    wenshu_info.penalty_prison_type = '有期徒刑' 


            AND wenshu_info.penalty_name = #{penalty_definite}


             AND wenshu_info.penalty_name in (select penalty_map.penalty_name_definite from penalty_map where penalty_map.penalty_class = #{penalty_class})



       AND wenshu_info.penalty_prison_length >= #{length_start}



            AND wenshu_info.penalty_prison_length < #{length_end}





LIMIT #{page_start},#{page_size}

select IFNULL(SUM(prison_length_num.number),0) from prison_length_num 




            AND prison_length_num.penalty_name = #{penalty_definite}


             AND prison_length_num.penalty_name in (select penalty_map.penalty_name_definite from penalty_map where penalty_map.penalty_class = #{penalty_class})



        AND prison_length_num.penalty_prison_length >= #{length_start}



